<?php

namespace Appstore\Bundle\TallyBundle\Repository;
use Appstore\Bundle\TallyBundle\Entity\ItemMetaAttribute;
use Doctrine\ORM\EntityRepository;

/**
 * ItemMeatAttributeRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ItemMeatAttributeRepository extends EntityRepository
{
    public function insertProductCategoryMeta($reEntity,$data)
    {

        $em = $this->_em;
        $i=0;

        if(isset($data['attributeId'])){
            foreach ($data['attributeId'] as $value) {

                $metaAttribute = $this->_em->getRepository('TallyBundle:ItemMetaAttribute')->findOneBy(array('product'=>$reEntity,'categoryMeta' => $value));
                if(!empty($metaAttribute)){
                    exit;
                    $this->updateMetaAttribute($metaAttribute,$data['value'][$i]);
                }else{
                    exit;
                    $itemAttribute= $this->_em->getRepository('TallyCategoryMeta.php')->find($value);
                    $entity = new ItemMetaAttribute();
                    $entity->setProduct($reEntity);
                    $entity->setValue($data['value'][$i]);
                    $entity->setCategoryMeta($itemAttribute);
                    $em->persist($entity);
                }
                $i++;
            }
            $em->flush();
        }

    }


    public function updateMetaAttribute(ItemMetaAttribute $metaAttribute,$value)
    {
            $em = $this->_em;
            $metaAttribute->setValue($value);
            $em->flush();
    }

    public function insertProductMeta($reEntity,$data)
    {

        $em = $this->_em;
        $i=0;

        if(isset($data['attributeId'])){

            foreach ($data['attributeId'] as $value) {

                $metaAttribute = $this->_em->getRepository('TallyBundle:ItemMetaAttribute')->findOneBy(array('voucherItem'=>$reEntity,'categoryMeta' => $value));
                if(!empty($metaAttribute)){
                    $this->updateMetaAttribute($metaAttribute,$data['value'][$i]);
                }else{
                    $itemAttribute= $this->_em->getRepository('TallyCategoryMeta.php')->find($value);
                    $entity = new ItemMetaAttribute();
                    $entity->setValue($data['value'][$i]);
                    $entity->setCategoryMeta($itemAttribute);
                    $entity->setVoucherItem($reEntity);
                    $em->persist($entity);
                }
                $i++;
            }
            $em->flush();
        }
    }



}
